import React from "react";
import Link from "next/link";

const Pesquisa = () => {
  const save = async () => {
    const form = {
      Nome: "aa",
      Email: "bb",
      Whatsapp: "cc",
    };
    try {
      const response = await fetch("/api/save", {
        method: "POST",
        body: JSON.stringify(form),
      });
      const data = await response.json();
      console.log(data);
    } catch (error) {}
  };
  return (
    <div className="pt-6">
      <h1 className="text-center font-bold my-4 text-2xl">
        Críticas e Sugestões
      </h1>
      <p className="mb-6 text-center">
        O restaurante X sempre busca atender melhor seus clientes.
        <br />
        Por isso, estamos sempre abertos a ouvir sua opinião
      </p>
      <div className="w-1/4 mx-auto">
        <label className="font-bold">Seu nome:</label>
        <input
          type="text"
          className="p-4 block shadow bg-blue-100 m-2 rounded my-2"
        />
        <button
          className="bg-blue-400 px-12 py-4 font-bold rounded-lg shadow-lg hover:shadow"
          onClick={save}
        >
          Salvar
        </button>
      </div>
    </div>
  );
};

export default Pesquisa;
