import React, { useState } from "react";
import Link from "next/link";

const Pesquisa = () => {
  const [form, setForm] = useState({
    Nome: "",
    Email: "",
    Whatsapp: "",
    Nota: 5
  });

  const [sucess, setSucess] = useState(false);
  const [retorno, setRetorno] = useState({});

  const save = async () => {
    return 1
    try {
      const response = await fetch("/api/save", {
        method: "POST",
        body: JSON.stringify(form),
      });
      const data = await response.json();
      setSucess(true);
      setRetorno(data);
    } catch (err) {}
  };
  const onChange = (evt) => {
    const value = evt.target.value;
    const key = evt.target.name;
    setForm((old) => ({
      ...old,
      [key]: value,
    }));
  };
  return (
    <div className="pt-6">
      <h1 className="text-center font-bold my-4 text-2xl">
        Críticas e Sugestões
      </h1>
      <p className="mb-6 text-center">
        A hamburgueria <b>The Burger Shack</b> sempre busca atender melhor seus
        clientes.
        <br />
        Por isso, estamos sempre abertos a ouvir sua opinião
      </p>
      {!sucess && (
        <div className="w-1/4 mx-auto">
          <label className="font-bold">Seu nome:</label>
          <input
            type="text"
            className="p-4 block shadow bg-blue-100 m-2 rounded my-2"
            placeholder="Nome"
            onChange={onChange}
            name="Nome"
            value={form.Nome}
          />
          <label className="font-bold">E-mail:</label>
          <input
            type="text"
            className="p-4 block shadow bg-blue-100 m-2 rounded my-2"
            placeholder="E-mail"
            onChange={onChange}
            name="Email"
            value={form.Email}
          />
          <label className="font-bold">Whatsapp:</label>
          <input
            type="text"
            className="p-4 block shadow bg-blue-100 m-2 rounded my-2"
            placeholder="Whatsapp"
            onChange={onChange}
            name="Whatsapp"
            value={form.Whatsapp}
          />

          <input type='radio' name='Nota' value='1' />
          <input type='radio' name='Nota' value='2' />
          <input type='radio' name='Nota' value='3' />
          <input type='radio' name='Nota' value='4' />
          <input type='radio' name='Nota' value='5' />
          <button
            className="bg-yellow-500 px-12 py-4 font-bold text-white rounded-lg shadow-lg hover:shadow mb-2"
            onClick={save}
          >
            Salvar
          </button>
        </div>
      )}
      {sucess && (
        <div className="w-1/4 mx-auto">
          <p className='mb-6 text-center bg-yellow-300 border-t border-b border-yellow-500 text-yellow-700 px-4 py-3' role="alert">Obrigado por contribuir com sua sugestão ou crítica</p>
          {
            retorno.showCoupon && 
            <div className='text-center border p-4 mb-4'>
              Ser cupom: <br />
              <span className='font-bold text-2xl'>{retorno.Cupom}</span>
            </div>
          }
          {
            retorno.showCoupon && 
            <div className='text-center border p-4 mb-4'>
              <span className='font-bold block mb-2'>{retorno.Promo}</span>
              <br />
              <span className='italic'>Tire um print ou foto desta tela e apresente ao pagar.</span>
            </div>
          }

        </div>
      )}
    </div>
  );
};

export default Pesquisa;
